    <section class="section-padding" id="booking">
      <div class="container">
          <div class="row">

              <div class="col-lg-8 col-12 mx-auto">
                  <div class="booking-form">

                      <h2 class="text-center mb-lg-3 mb-2">Book an appointment</h2>

                      <form #userlogin="ngForm" (ngSubmit)="onSubmit(userlogin)">
                          <div class="row">
                              <div class="col-lg-6 col-12">
                                  <input type="text" name="name" id="name" class="form-control" placeholder="Full name" ngModel required>
                              </div>

                              <div class="col-lg-6 col-12">
                                  <input type="email" name="email" id="email" pattern="[^ @]*@[^ @]*" class="form-control" ngModel placeholder="Email address" required>
                              </div>

                              <div class="col-lg-6 col-12">
                                  <input type="telephone" name="phone" id="phone" pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}" class="form-control" ngModel placeholder="Phone: 123-456-7890">
                              </div>

                              <div class="col-lg-6 col-12">
                                  <input type="date" name="date" id="date" value="" class="form-control">

                              </div>

                              <div class="col-12">
                                  <textarea class="form-control" rows="5" id="message" name="message" placeholder="Additional Message"></textarea>
                              </div>

                              <div class="col-lg-3 col-md-4 col-6 mx-auto">
                                  <button type="submit" class="form-control" id="submit-button"  >Book Now</button>
                              </div>
                          </div>
                      </form>

                  </div>
              </div>

          </div>
      </div>
  </section>

</div>

